(()=>{const e=document.querySelectorAll(".nav-button"),t=document.querySelector("#nav-toggle");e.forEach((e=>e.addEventListener("click",(function(){event.stopPropagation(),document.querySelector(".nav-active").classList.toggle("nav-active"),e.classList.toggle("nav-active")})))),t.addEventListener("click",(function(){const e=document.querySelector("#side-panel"),o=document.querySelector("nav"),l=document.querySelectorAll(".nav-text");e.classList.toggle("side-panel-open"),e.classList.toggle("side-panel-closed"),o.classList.toggle("nav-open"),o.classList.toggle("nav-closed"),t.classList.toggle("nav-toggle-open"),t.classList.toggle("nav-toggle-closed"),l.forEach((e=>{e.classList.toggle("nav-text-open"),e.classList.toggle("nav-text-closed")}))}))})();